local meta = FindMetaTable("Player");function meta:SetupVariables()	//set up all the player variables here, this is shared		self.Frozen = false;	self:SetNWBool("UC_Voted", false);		if (SERVER) then			self.PlaybackRate = 1;				self.NextRank = 1;				self.LastUC = false;		else							end	endfunction meta:IsUC()	return (self == GAMEMODE:GetUC());endfunction GM:NewSalsa()endfunction GM:SetSalsa()endfunction GM:GetSalsa()	return nil;endfunction GM:SalsaCheck()endfunction GM:JumpThink()endfunction meta:IsSalsa()	return false;endfunction meta:IsTaunting()	return self:GetNWBool("UC_Taunting", false);endfunction meta:CanTaunt()	if (!self:IsTaunting() && !self:IsScared() && !self:GetSprinting() && self:IsOnGround() && self:Team() == GAMEMODE.TEAM_PIGS) then		return true;	end	return false;endfunction meta:MovementKeyDown()		if (self:KeyDown(IN_FORWARD) || self:KeyDown(IN_BACK) || self:KeyDown(IN_MOVELEFT) || self:KeyDown(IN_MOVERIGHT)) then		return true;		end		return false;	endfunction meta:IsDead()	return self:GetNWBool("UCDead", true);endmeta.Alive = function(ply)	if (ply:IsGhost() || ply:IsDead()) then		return false;	end	return true;endif (SERVER) then		function meta:SetDead(b)		self:SetNWBool("UCDead", b);	end	//LETS OVERRIDE SOME KILL FUNCTION YEAAAAA	meta.Kill = function(ply) //yea woo				if (!ply:Alive()) then			return;		end				hook.Call("DoPlayerDeath", GAMEMODE, ply);		hook.Call("PlayerDeath", GAMEMODE, ply);				ply:SetDead(true);				if (ply:FlashlightIsOn()) then			ply:Flashlight();		end				if (ply:IsUC()) then			ply:CreateBirdProp();			ply:CreateRagdoll();		else					if (!ply:IsGhost()) then				ply:SetGhost(true);			end						if (ply.Squished || ply.Bit) then							if (team.AlivePlayers(ply:Team()) > 0) then					GAMEMODE:AddTime(20);				end							if (ply.Squished) then					local r, g, b = ply:GetRankColor();					local effectdata = EffectData();						effectdata:SetOrigin(ply:GetPos());						effectdata:SetStart(Vector(r, g, b));					util.Effect("piggy_pop", effectdata);					ply:SetPancake(false);				else					ply:CreateRagdoll();				end											else				ply:CreateRagdoll();			end					end				GAMEMODE:DoKillNotice(ply);			end	function meta:SetupSpeeds()		local spd, cspd = 175, .3;		if ((GAMEMODE:IsPlaying() || GAMEMODE:GetState() == GAMEMODE.STATE_ENDCOUNTDOWN) && self:IsUC()) then						spd = 112;			cspd = 1;					end				if (self:IsSalsa()) then			spd = 300;			cspd = 1;		end			if (self:IsGhost()) then			spd, cspd = 250, 1;		end			self:SetSpeed(spd);		self:SetCrouchedWalkSpeed(cspd);			end			function meta:UpdateSpeeds()				if (!GAMEMODE:IsPlaying()) then			self:SetupSpeeds();		end				if (self:GetSprinting() || self:IsScared()) then						local spd, cspd = 375, .5;									if (self:IsScared()) then				spd = 242;				cspd = .5;			end						if (self:IsUC()) then								spd = 300;							end						self:SetSpeed(spd);			self:SetCrouchedWalkSpeed(cspd);					else						self:ResetSpeeds();					end				if (self:GetNWBool("Swiped", false)) then			self:SetSpeed(0);		end			end		function meta:SetSpeed(spd)		self:SetWalkSpeed(spd);		self:SetRunSpeed(spd);	end		function meta:ResetSpeeds()		self:SetupSpeeds();	end				function meta:SetRankBodygroups()				if (self:HasCustomRank()) then			return;		end				local rank = self:GetRankNum();		if (rank > 3) then			self:SetBodygroup(2, 1);			self:SetBodygroup(1, 0);		else			self:SetBodygroup(2, 0);			self:SetBodygroup(1, (rank - 1));		end			end			function meta:SetRankSkin()				if (self:HasCustomRank()) then			return;		end				local rank = self:GetRankNum();		self:SetSkin((rank - 1));			end			function meta:MakePiggyNoises()		self.LastSnort = (self.LastSnort || (CurTime() + math.random(9, 14)));		if (CurTime() >= self.LastSnort) then						self:EmitSound("UCH/pigs/snort" .. tostring(math.random(1, 4)) .. ".mp3", 75, math.random(90, 105));						local num = math.Rand(6, 9);						if (self:IsScared() || !self:CanSprint()) then				num = (num * .25);			end						self.LastSnort = (CurTime() + num);					end	end			function meta:CreateUCHRagdoll(b)				/*umsg.Start("CreateUCHRagdoll")			umsg.Entity(self);			if (b) then				umsg.Entity(GAMEMODE:GetUC());			end		umsg.End()*/					if (self:IsGhost()) then			return;		end				if (ValidEntity(self.Ragdoll)) then			self.Ragdoll:Remove();		end				local rag = ents.Create("prop_ragdoll");		rag:SetModel(self:GetModel());		rag:SetPos((self:GetPos() + Vector(0, 0, 24)));		rag:SetAngles(self:GetAngles());		rag:Spawn();		if (!rag:IsValid()) then			return;		end		rag:SetCollisionGroup(COLLISION_GROUP_DEBRIS);		local entvel;		local entphys = self:GetPhysicsObject();		if (entphys:IsValid()) then			entvel = entphys:GetVelocity();		else			entvel = self:GetVelocity();		end		if (b) then			rag:EmitSound("UCH/pigs/squeal" .. tostring(math.random(1, 3)) .. ".mp3", 82, math.random(94, 105));		end		for i = 1, rag:GetPhysicsObjectCount() do			local bone = rag:GetPhysicsObjectNum(i);			if (ValidEntity(bone)) then				local bonepos, boneang = self:GetBonePosition(rag:TranslatePhysBoneToBone(i));				bone:SetPos(bonepos);				bone:SetAngle(boneang);								if (b && ValidEntity(GAMEMODE:GetUC())) then										local fwd = GAMEMODE:GetUC():GetForward();					fwd.z = 0;					fwd:Normalize();										entvel = (entvel + (fwd * 3200) + Vector(0, 0, 1600));									end								bone:ApplyForceOffset(self:GetVelocity(), self:GetPos());				bone:AddVelocity(entvel);							end		end				rag:SetSkin(self:GetSkin());		rag:SetColor(self:GetColor());		rag:SetMaterial(self:GetMaterial());				for i = 1, 2 do			rag:SetBodygroup(i, self:GetBodygroup(i));		end		self.Ragdoll = rag;				rag.CollideVar = true;				timer.Simple(32, function()			if (ValidEntity(rag) && rag != GAMEMODE.UCRagdoll) then				rag:Remove();			end		end);				return rag;			end			function meta:StopTaunting()		self:GetTauntEnd(); //if they have a custom taunt function, run it		self:SetNWBool("UC_Taunting", false);	end		function meta:Taunt(t, playback)			if (self:IsUC() || self:IsGhost() || self:IsSalsa()) then			return;		end			self:SetNWBool("UC_Taunting", true);					self:SetCycle(0);					local seq = self:LookupSequence(t);		self:ResetSequence(seq);				self:PlaybackRateOV(playback);				RestartAnimation(self);				self:GetTauntStart(); //if they have a custom taunt function, run it							timer.Simple((self:SequenceDuration() * (.98 / self:GetNWInt("PlaybackRate", 1))), function()			if (ValidEntity(self)) then							self:StopTaunting();				self:PlaybackReset();							end		end);			end			function meta:SetView(num)		self:SetViewOffset(Vector(0, 0, num));		self:SetViewOffsetDucked(Vector(0, 0, (num * .75)));	end			function meta:PlaySpawnSound()				if (self:IsGhost()) then			return;		end				self.LastSpawnSound = (self.LastSpawnSound || 0);				if (CurTime() >= self.LastSpawnSound) then						local rank = self:GetRank();			local ext = "mp3";						if (self:HasCustomRank()) then				rank = "custom";			end						if (self:IsUC()) then				rank = "chimera";			end						local snd = "UCH/music/cues/" .. rank:lower() .. "_spawn." .. ext;				self:SendLua("surface.PlaySound(\"" .. snd .. "\")");						self.LastSpawnSound = (CurTime() + SoundDuration(snd));				end			endelse			function meta:CreateRagdoll(b, ply)			local rag = ents.Create("hl2mp_ragdoll");		rag:SetModel(self:GetModel());		rag:SetPos(self:GetPos());		rag:SetAngles(self:GetAngles());		rag:Spawn();		if (!rag:IsValid()) then			return		end		rag:SetCollisionGroup(COLLISION_GROUP_DEBRIS);		local entvel;		local entphys = self:GetPhysicsObject();		if (entphys:IsValid()) then			entvel = entphys:GetVelocity();		else			entvel = self:GetVelocity();		end		for i = 1, rag:GetPhysicsObjectCount() do			local bone = rag:GetPhysicsObjectNum(i);			if (ValidEntity(bone)) then				local bonepos, boneang = self:GetBonePosition(rag:TranslatePhysBoneToBone(i));				bone:SetPos(bonepos);				bone:SetAngle(boneang);								if (b) then										local fwd = ply:GetForward();					fwd.z = 0;					fwd:Normalize();										entvel = (entvel + (fwd * 3200));									end								bone:ApplyForceOffset(self:GetVelocity(), self:GetPos());				bone:AddVelocity(entvel);							end		end		rag:SetSkin(self:GetSkin());		rag:SetColor(self:GetColor());		rag:SetMaterial(self:GetMaterial());				for i = 1, 2 do			rag:SetBodygroup(i, self:GetBodygroup(i));		end				return rag;			end		local function CreateRagdolls(um)		local ply = um:ReadEntity();		local UC = (um:ReadEntity() || nil);				local b = (UC != nil && true) || false;				local rag = ply:CreateRagdoll(b, UC);		timer.Simple(32, function()			if (ValidEntity(rag)) then				rag:Remove();			end		end);			end	usermessage.Hook("CreateUCHRagdoll", CreateRagdolls);		end